import gql from "graphql-tag";
import { Query } from "react-apollo";
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";

const client = new ApolloClient({
  uri: "http://localhost:5000/graphql",
});

const LAUNCHES_QUERY = gql`
  query LaunchesQuery($flight_number: Int!) {
    launch(flight_number: $flight_number) {
      flight_number
      mission_name
      launch_year
      launch_date_local
      launch_success
      details
    }
  }
`;

export default ({ flightNumber }) => {
  console.log(flightNumber);
  flight_number = parseInt(flightNumber);
  return (
    <ApolloProvider client={client}>
      <Query query={LAUNCHES_QUERY} variables={flightNumber}>
        {({ loading, error, data }) => {
          if (loading) return <h4>Loading...</h4>;
          if (error) console.log(error);

          const {
            mission_name,
            flight_number,
            launch_year,
            launch_success,
            details,
          } = data.launch;
          return <div>Flight Number: {flight_number}</div>;
        }}
      </Query>
      <div>
        <h2>Flight</h2>
      </div>
    </ApolloProvider>
  );
};

export const getServerSideProps = async (req) => {
  const res = req.params.flight_number;
  return { props: { flightNumber: res } };
};
